import React from 'react';
import { useNavigate } from 'react-router-dom';
import TrainerLayout from '../components/TrainerLayout';

const dummyMeals = {
  breakfast: [
    { name: 'Oats', ingredients: 'rasberry, oats, milk', img: 'data:image/jpeg;base64,' },
    { name: 'Scrambled egg', ingredients: 'egg, pepper', img: 'data:image/jpeg;base64,' },
  ],
  lunch: [
    { name: 'Kabsah', ingredients: 'chicken, rice', img: 'https://modo3.com/thumbs/fit630x300/50901/1591020560/%D8%B7%D8%B1%D9%8A%D9%82%D8%A9_%D9%83%D8%A8%D8%B3%D8%A9_%D8%A7%D9%84%D8%AF%D8%AC%D8%A7%D8%AC_%D8%A7%D9%84%D8%B3%D8%B9%D9%88%D8%AF%D9%8A%D8%A9.jpg' },
  ],
  dinner: [
    { name: 'Meat ball', ingredients: 'spaghetti, meat', img: 'https://cdn.loveandlemons.com/wp-content/uploads/2020/02/spaghetti-and-meatballs.jpg' },
    { name: 'Protein Soap', ingredients: 'chicken, bean, potato', img: 'data:image/jpeg;base64,' },
  ],
  snacks: [
    { name: 'Popcorn', img: 'data:image/jpeg;base64,' },
  ],
};

const MaintainWeightMeals = () => {
  const navigate = useNavigate();
  const formattedTitle = "Maintain Weight";

  const handleAddMeal = (category) => {
    navigate(`/add-meal/maintain-weight/${category}`);
  };

  const categories = Object.keys(dummyMeals);

  return (
    <TrainerLayout pageTitle="Manage Client">
      <h2 className="text-xl font-semibold text-center mb-8">{formattedTitle}</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        {categories.filter(cat => cat !== 'snacks').map(category => (
          <div key={category} className="bg-[#B7E4C7] p-4 rounded-xl shadow">
            <h3 className="text-lg font-semibold text-center capitalize mb-4">{category}</h3>
            <div className="space-y-3">
              {dummyMeals[category].map((meal, index) => (
                <div key={index} className="bg-white flex items-center gap-3 p-2 rounded shadow">
                  <img src={meal.img} alt={meal.name} className="w-12 h-12 object-cover rounded-full" />
                  <div>
                    <p className="font-semibold">{meal.name}</p>
                    {meal.ingredients && <p className="text-sm text-gray-500">{meal.ingredients}</p>}
                  </div>
                </div>
              ))}
            </div>
            <button onClick={() => handleAddMeal(category)} className="flex justify-center items-center w-8 h-8 rounded-full bg-white mt-4 mx-auto shadow text-xl font-bold">+</button>
          </div>
        ))}
      </div>

      <div className="bg-[#B7E4C7] p-4 rounded-xl shadow max-w-5xl mx-auto">
        <h3 className="text-lg font-semibold text-center capitalize mb-4">Snacks</h3>
        <div className="flex flex-wrap justify-center gap-4">
          {dummyMeals.snacks.map((meal, index) => (
            <div key={index} className="bg-white flex items-center gap-3 p-2 rounded shadow w-48">
              <img src={meal.img} alt={meal.name} className="w-10 h-10 object-cover rounded-full" />
              <p className="font-semibold">{meal.name}</p>
            </div>
          ))}
        </div>
        <button onClick={() => handleAddMeal('snacks')} className="flex justify-center items-center w-8 h-8 rounded-full bg-white mt-4 mx-auto shadow text-xl font-bold">+</button>
      </div>
    </TrainerLayout>
  );
};

export default MaintainWeightMeals;